mapper.select:
select *
from Assistant
where AS_IsPass = false;

select *
from Assistant
where AS_IsIdle = true;

mapper.update:
update Assistant
SET AS_Balance = #{Balance}
WHERE AS_Account = #{AS_Account};

update Customer
SET CU_Balance = #{Balance}
WHERE CU_Account = #{CU_Account};

UPDATE Assistant
SET AS_IsPass = true
WHERE AS_Account = #{AS_Account};
